<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">    allowEmptyIssuePrefixs: </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">false</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">,</span></span>
